mapper.select:
select
  id, name, type_id, icon, url, create_time, update_time
from website


        and id = #{id}


        and name = #{name}


        and type_id = #{typeId}


        and icon = #{icon}


        and url = #{url}


        and create_time = #{createTime}


        and update_time = #{updateTime}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from website


        and id = #{id}


        and name = #{name}


        and type_id = #{typeId}


        and icon = #{icon}


        and url = #{url}


        and create_time = #{createTime}


        and update_time = #{updateTime}
